 const [todos, setTodos] = useState<Todo[]>([]);

  const addToDoHandler = (todoText: string) => {};

  return (
    <div className="App">
      <NewTodo onAddTodo={addToDoHandler}/>
      <Todos items={todos}/>
    </div>
  );
}

export default App;
